textDiyStyle:
color: #333;
background: linear-gradient(
  45deg,
  LightGreen 20%,
  LightPink 20% 40%,
  LightSalmon 40% 60%,
  LightSeaGreen 60% 80%,
  LightSkyBlue 80%
);
&:hover {
  color: #111;
};

opacity: 0.92;
-webkit-opacity: 0.92;
display: block;
padding: 0.3em 0.75em;
border-left: 0.25em solid #cc3355;
background: #302e34;
border-radius: 0 0.25em 0.25em 0;
box-shadow: 0 2px 8px rgba(0, 0, 0, 0.3);
transition: opacity 0.2s ease, box-shadow 0.2s ease, background 0.2s ease;
&:hover {
  opacity: 1;
  -webkit-opacity: 1;
  background: #3a3840;
  box-shadow: 0 6px 16px rgba(0, 0, 0, 0.5);
}
